INSERT INTO `enfermedad` (`codigo_enf`, `nombre_enf`, `tipo_enf`, `descripcion_enf`, `sintomas_enf`) VALUES ('E014', 'EPOC', 'Respiratoria', 'Enfermedad pulmonar obstructiva crónica que causa flujo de aire obstruido desde los pulmones.', 'Tos crónica, dificultad para respirar, producción de esputo.');
INSERT INTO `enfermedad` (`codigo_enf`, `nombre_enf`, `tipo_enf`, `descripcion_enf`, `sintomas_enf`) VALUES ('E015', 'Parkinson', 'Neurológica', 'Trastorno del sistema nervioso que afecta el movimiento.', 'Temblores, rigidez muscular, lentitud de movimientos.');
INSERT INTO `enfermedad` (`codigo_enf`, `nombre_enf`, `tipo_enf`, `descripcion_enf`, `sintomas_enf`) VALUES ('E016', 'Alzheimer', 'Neurológica', 'Enfermedad neurodegenerativa que causa problemas con la memoria, el pensamiento y el comportamiento.', 'Pérdida de memoria, confusión, cambios de comportamiento.');
INSERT INTO `enfermedad` (`codigo_enf`, `nombre_enf`, `tipo_enf`, `descripcion_enf`, `sintomas_enf`) VALUES ('E017', 'Osteoporosis', 'Ósea', 'Condición que debilita los huesos, haciéndolos frágiles y más propensos a romperse.', 'Dolor de espalda, pérdida de estatura, fracturas óseas.');
INSERT INTO `enfermedad` (`codigo_enf`, `nombre_enf`, `tipo_enf`, `descripcion_enf`, `sintomas_enf`) VALUES ('E018', 'Anemia', 'Hematológica', 'Condición en la que no hay suficientes glóbulos rojos sanos para transportar oxígeno adecuadamente a los tejidos del cuerpo.', 'Fatiga, debilidad, piel pálida.');
INSERT INTO `enfermedad` (`codigo_enf`, `nombre_enf`, `tipo_enf`, `descripcion_enf`, `sintomas_enf`) VALUES ('E019', 'Esquizofrenia', 'Psiquiátrica', 'Trastorno mental grave que afecta la capacidad de una persona para pensar, sentir y comportarse con claridad.', 'Alucinaciones, delirios, trastornos del pensamiento.');
INSERT INTO `enfermedad` (`codigo_enf`, `nombre_enf`, `tipo_enf`, `descripcion_enf`, `sintomas_enf`) VALUES ('E020', 'Obesidad', 'Metabólica', 'Condición médica en la que el exceso de grasa corporal puede afectar la salud.', 'Aumento de peso, fatiga, dificultad para respirar.');
INSERT INTO `enfermedad` (`codigo_enf`, `nombre_enf`, `tipo_enf`, `descripcion_enf`, `sintomas_enf`) VALUES ('E021', 'Diabetes Tipo 1', 'Metabólica', 'Enfermedad crónica en la que el páncreas produce poca o ninguna insulina.', 'Sed excesiva, hambre extrema, pérdida de peso.');
INSERT INTO `enfermedad` (`codigo_enf`, `nombre_enf`, `tipo_enf`, `descripcion_enf`, `sintomas_enf`) VALUES ('E022', 'Diabetes Tipo 2', 'Metabólica', 'Trastorno en el cual el cuerpo no utiliza insulina de manera adecuada.', 'Sed excesiva, micción frecuente, fatiga.');
INSERT INTO `enfermedad` (`codigo_enf`, `nombre_enf`, `tipo_enf`, `descripcion_enf`, `sintomas_enf`) VALUES ('E023', 'Artritis Reumatoide', 'Autoinmune', 'Trastorno inflamatorio crónico que afecta las articulaciones.', 'Dolor en las articulaciones, hinchazón, rigidez matutina.');
INSERT INTO `enfermedad` (`codigo_enf`, `nombre_enf`, `tipo_enf`, `descripcion_enf`, `sintomas_enf`) VALUES ('E024', 'Lupus', 'Autoinmune', 'Enfermedad autoinmune que causa inflamación en diferentes partes del cuerpo.', 'Fatiga, dolor en las articulaciones, erupciones cutáneas.');
INSERT INTO `enfermedad` (`codigo_enf`, `nombre_enf`, `tipo_enf`, `descripcion_enf`, `sintomas_enf`) VALUES ('E025', 'Asma', 'Respiratoria', 'Enfermedad crónica que inflama y estrecha las vías respiratorias.', 'Dificultad para respirar, sibilancias, tos.');
INSERT INTO `enfermedad` (`codigo_enf`, `nombre_enf`, `tipo_enf`, `descripcion_enf`, `sintomas_enf`) VALUES ('E026', 'Hipertensión', 'Cardiovascular', 'Condición en la que la fuerza de la sangre contra las paredes de las arterias es demasiado alta.', 'Dolor de cabeza, mareos, visión borrosa.');
INSERT INTO `enfermedad` (`codigo_enf`, `nombre_enf`, `tipo_enf`, `descripcion_enf`, `sintomas_enf`) VALUES ('E027', 'Hepatitis B', 'Infecciosa', 'Infección viral que causa inflamación del hígado.', 'Ictericia, fatiga, dolor abdominal.');
INSERT INTO `enfermedad` (`codigo_enf`, `nombre_enf`, `tipo_enf`, `descripcion_enf`, `sintomas_enf`) VALUES ('E028', 'Hepatitis C', 'Infecciosa', 'Infección viral que causa inflamación del hígado y puede llevar a daño hepático grave.', 'Fatiga, fiebre, dolor en las articulaciones.');
INSERT INTO `enfermedad` (`codigo_enf`, `nombre_enf`, `tipo_enf`, `descripcion_enf`, `sintomas_enf`) VALUES ('E029', 'COVID-19', 'Infecciosa', 'Enfermedad causada por el coronavirus SARS-CoV-2.', 'Fiebre, tos, dificultad para respirar.');
INSERT INTO `enfermedad` (`codigo_enf`, `nombre_enf`, `tipo_enf`, `descripcion_enf`, `sintomas_enf`) VALUES ('E030', 'Migraña', 'Neurológica', 'Dolor de cabeza recurrente e intenso que puede causar náuseas y sensibilidad a la luz.', 'Dolor de cabeza severo, náuseas, sensibilidad a la luz.');
INSERT INTO `enfermedad` (`codigo_enf`, `nombre_enf`, `tipo_enf`, `descripcion_enf`, `sintomas_enf`) VALUES ('E031', 'Esclerosis Múltiple', 'Neurológica', 'Enfermedad crónica que afecta el sistema nervioso central.', 'Fatiga, problemas de equilibrio, visión borrosa.');
INSERT INTO `enfermedad` (`codigo_enf`, `nombre_enf`, `tipo_enf`, `descripcion_enf`, `sintomas_enf`) VALUES ('E032', 'Enfermedad Celíaca', 'Autoinmune', 'Trastorno en el cual la ingestión de gluten daña el intestino delgado.', 'Diarrea, dolor abdominal, pérdida de peso.');
